/**
 * render songs
 * scroll top
 * play/pause/seek
 * cd rotate
 * next/prev
 * random
 * next/repeat when ended
 * active song
 * scroll active song into view
 * play song when click
 */

const PLAYER_STORAGE_KEY = "RANDOM_REPEAT";

const $ = document.querySelector.bind(document);

const player = $(".player");
const heading = $("header h2");
const cdThumb = $(".cd-thumb");
const cdInner = $(".cd-inner");
const audio = $("#audio");
const cd = $(".cd");
const playBtn = $(".btn-toggle-play");
const progress = $("#progress");
const btnNext = $(".btn-next");
const btnPrev = $(".btn-prev");
const btnRandom = $(".btn-random");
const btnRepeat = $(".btn-repeat");
const playList = $(".playlist");
//weather
// var seach = document.querySelector(".seach");
// var city = document.querySelector(".city");
// var country = document.querySelector(".country");
// var value = document.querySelector(".value");
// var visibility = document.querySelector(".visibility span");
// var wind = document.querySelector(".wind span");
// var sun = document.querySelector(".sun span");
// var content = document.querySelector(".content");
// var body = document.querySelector("body");

const app = {
  currentIndex: 0,
  isPlaying: false,
  isRandom: false,
  isRepeat: false,
  config: JSON.parse(localStorage.getItem(PLAYER_STORAGE_KEY)) || {},
  songs: [
    {
      name: "Trịnh Đình Quang",
      singer: "Em Mỉm Cười",
      path: "./asset/music/Em Mỉm Cười Trông Thật Đẹp.mp3",
      image:
        "data:image/jpeg;base64,",
    },
    {
      name: "Summertime",
      singer: "K-391",
      path: "./asset/music/K-391 - Summertime.mp3",
      image:
        "data:image/jpeg;base64,",
    },
    {
      name: "Nevada",
      singer: "Vicetone",
      path: "./asset/music/Vicetone-Nevada-Remix.mp3",
      image: "./asset/img/nevada.jfif",
    },
    {
      name: "Tiểu Thuyết Tình Yêu",
      singer: "Lee7 ft Andree",
      path: "./asset/music/Tiểu Thuyết Tình Yêu - Lee7 ft Andree.mp3",
      image:
        "data:image/jpeg;base64,",
    },
    {
      name: "Kẻ Theo Đuổi Ánh Sáng ",
      singer: "Huy Vạc x Tiến Nguyễn",
      path: "./asset/music/Kẻ Theo Đuổi Ánh Sáng - Huy Vạc x Tiến Nguyễn.mp3",
      image:
        "data:image/jpeg;base64,",
    },
    {
      name: "虹",
      singer: "Masaki suda",
      path: "./asset/music/虹.mp3",
      image:
        "data:image/jpeg;base64,",
    },
  ],
  setConfig: function (key, value) {
    this.config[key] = value;
    localStorage.setItem(PLAYER_STORAGE_KEY, JSON.stringify(this.config));
  },
  render: function () {
    const htmls = this.songs.map((song, index) => {
      return `
            <div  class="song ${
              index === this.currentIndex ? "active" : ""
            }" data-index=${index}>
            <div
              class="thumb"
              style="
                background-image: url('${song.image}');
              "
            ></div>
            <div class="body">
              <h3 class="title">${song.name}</h3>
              <p class="author">${song.singer}</p>
            </div>
            <div class="spectrum">
              <div></div>
            </div>
            <div class="option">
              <i class="fas fa-ellipsis-h"></i>
            </div>
          </div>
            `;
    });
    $(".playlist").innerHTML = htmls.join("");
  },
  defineProperties: function () {
    Object.defineProperty(this, "currentSong", {
      get: function () {
        return this.songs[this.currentIndex];
      },
    });
  },
  scrollActiveSong: function () {
    setTimeout(() => {
      $(".song.active").scrollIntoView({
        behavior: "smooth",
        block: "nearest",
      });
    }, 300);
  },
  loadCurrentSong: function () {
    heading.textContent = this.currentSong.name;
    cdThumb.style.backgroundImage = `url('${this.currentSong.image}')`;
    audio.src = this.currentSong.path;
  },
  loadConfig: function () {
    this.isRandom = this.config.isRandom;
    this.isRepeat = this.config.isRepeat;
  },
  handleEvents: function () {
    const _this = this;
    const cdWidth = cd.offsetWidth;
    //xử lí cd quay/dừng
    const cdThumbAnimate = cdThumb.animate([{ transform: "rotate(360deg)" }], {
      duration: 10000,
      iterations: Infinity,
    });
    cdThumbAnimate.pause();

    //xử lí phóng to thu nhỏ cd
    document.onscroll = function () {
      const scrollTop = window.screenY || document.documentElement.scrollTop;
      const newCdWidth = cdWidth - scrollTop;
      cd.style.width = newCdWidth > 0 ? newCdWidth + "px" : 0;
      cd.style.opacity = newCdWidth / cdWidth;
    };
    //khi click play
    playBtn.onclick = function () {
      if (_this.isPlaying) {
        audio.pause();
      } else {
        audio.play();
      }
    };
    //khi song dc play
    audio.onplay = function () {
      _this.isPlaying = true;
      player.classList.add("playing");
      cdInner.classList.add("active");
      $(".song.active .spectrum").classList.remove("paused");
      $(".song.active .spectrum").classList.add("active");
      cdThumbAnimate.play();
    };
    audio.onpause = function () {
      _this.isPlaying = false;
      player.classList.remove("playing");
      cdInner.classList.remove("active");
      $(".song.active .spectrum").classList.add("paused");
      $(".song.active .spectrum").classList.remove("active");
      cdThumbAnimate.pause();
    };
    //khi tiến độ bài hát thay đổi
    audio.ontimeupdate = function () {
      if (audio.duration) {
        const progressPercent = Math.floor(
          (audio.currentTime / audio.duration) * 100
        );
        progress.value = progressPercent;
      }
    };
    //xử lí khi tua (có bug khi bấm chậm)
    progress.onchange = function (e) {
      const seekTime = (audio.duration / 100) * e.target.value;
      audio.currentTime = seekTime;
    };
    // next/prev
    btnNext.onclick = function () {
      if (_this.isRandom) {
        _this.randomSong();
      } else {
        _this.nextSong();
      }
      audio.play();
      //render lại để song sau nhận active
      _this.render();
      _this.scrollActiveSong();
    };
    btnPrev.onclick = function () {
      if (_this.isRandom) {
        _this.randomSong();
      } else {
        _this.prevSong();
      }
      audio.play();
      _this.render();
    };
    //bật/tắt active randombtn
    btnRandom.onclick = function (e) {
      _this.isRandom = !_this.isRandom;
      _this.setConfig("isRandom", _this.isRandom);
      btnRandom.classList.toggle("active", _this.isRandom);
    };
    //xử lí phát lại song
    btnRepeat.onclick = function () {
      _this.isRepeat = !_this.isRepeat;
      _this.setConfig("isRandom", _this.isRepeat);

      btnRepeat.classList.toggle("active", _this.isRepeat);
    };
    //xử lí next khi song ended
    audio.onended = function () {
      if (_this.isRepeat) {
        audio.play();
      } else {
        btnNext.click();
      }
    };
    //lắng nghe click vào playlist
    playList.onclick = function (e) {
      const songNode = e.target.closest(".song:not(.active)");
      //xử lí click vào song
      if (songNode || !songNode.closest("option")) {
        _this.currentIndex = Number(songNode.dataset.index);
        _this.loadCurrentSong();
        _this.render();
        audio.play();
        //xử lí khi click vào song
        if (songNode) {
          e.target.getAttribute("data-index");
        }
        //xử lí khi click vào song
        if (e.target.closest(".option")) {
        }
      }
    };
  },
  nextSong: function () {
    this.currentIndex++;
    if (this.currentIndex >= this.songs.length) {
      this.currentIndex = 0;
    }
    this.loadCurrentSong();
  },
  prevSong: function () {
    this.currentIndex--;
    if (this.currentIndex < 0) {
      this.currentIndex = this.songs.length - 1;
    }
    this.loadCurrentSong();
  },

  randomSong: function () {
    let newIndex;
    do {
      newIndex = Math.floor(Math.random() * this.songs.length);
    } while (newIndex === this.currentIndex); //đk lặp, ko trùng bài cũ
    this.currentIndex = newIndex;
    this.loadCurrentSong();
  },

  start: function () {
    //lấy config từ localstorage add vào
    this.loadConfig();
    //đn các thuộc tính cho object
    this.defineProperties();
    //lắng nghe xử lí các sự kiện
    this.handleEvents();
    //tải thông tin bài hát đầu tiên vào UI khi chạy ứng dụng
    this.loadCurrentSong();
    //render playlist
    this.render();
    //hiển thị trạng thái
    btnRandom.classList.toggle("active", this.isRandom);
    btnRepeat.classList.toggle("active", this.isRepeat);
  },
};
app.start();

